import React from "react";
import { Typography, Checkbox, Grid } from "@material-ui/core";

class Item extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      task: this.props.item.task,
      complete: this.props.item.complete
    };
  }
  handleChange = e => {
    this.setState({
      complete: e.target.checked
    });
  };
  render() {
    return (
      <div>
        <Grid container alignItems="left" justify="left">
          <Grid item xs={1} sm={1} md={1}>
            <Checkbox
              type="checkbox"
              onChange={this.handleChange}
              checked={this.state.complete}
            />
          </Grid>
          <Grid item xs={11} sm={11} md={11}>
            <Typography
              className={this.state.complete && "taskComplete"}
              variant="subtitle2"
              style={{ paddingTop: "12px", paddingBottom: "0" }}
            >
              {this.state.task}
            </Typography>
          </Grid>
        </Grid>
        <hr />
      </div>
    );
  }
}

export default Item;
