import React from "react";

import ListItem from "./Item.js";

class TodoList extends React.Component {
  constructor() {
    super();
    this.state = {
      list: [
        {
          id: 1,
          task: "Go home",
          completed: false
        },
        {
          id: 2,
          task: "Take test",
          completed: false
        },
        {
          id: 3,
          task: "Perform",
          completed: false
        }
      ],
      task: {
        id: 0,
        task: "",
        completed: false
      }
    };
  }

  render() {
    const listItems = this.state.list.map(item => <ListItem item={item} />);
    return (
      <div className="listMainContainer">
        <div className="listContainer">{listItems}</div>
      </div>
    );
  }
}
export default TodoList;
