import React from "react";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import Grid from "@material-ui/core/Grid";

import Item from "./Item.js";
import AddItem from "./AddItem";

class TodoList extends React.Component {
  constructor() {
    super();
    this.state = {
      list: [],
      maxId: 0,
      showPopup: false
    };
  }
  handleClick = e => {
    this.setState({
      showPopup: true
    });
  };

  closePopup = () => {
    this.setState({
      showPopup: false
    });
  };

  saveTask = task => {
    this.setState(prevState => {
      let newList = prevState.list;
      newList.push({
        id: prevState.maxId + 1,
        task: task,
        completed: false
      });
      return {
        list: newList,
        maxId: prevState.maxId + 1,
        showPopup: false
      };
    });
  };

  render() {
    const listItems = this.state.list.map(item => (
      <div>
        <Item item={item} />
      </div>
    ));
    return (
      <div className="listMainContainer">
        <div className="listContainer">{listItems}</div>
        <Grid container>
          <Grid item md={10} sm={6} xs={3}></Grid>
          <Grid item md={2} sm={6} xs={9}>
            {this.state.showPopup && (
              <AddItem closePopup={this.closePopup} saveTask={this.saveTask} />
            )}
            <Fab color="primary" aria-label="add" className="AddIcon">
              <AddIcon onClick={this.handleClick} />
            </Fab>
          </Grid>
        </Grid>
      </div>
    );
  }
}
export default TodoList;
