import React from "react";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import Grid from "@material-ui/core/Grid";

import Item from "./Item.js";
import SimplePopover from "./Popup";

class TodoList extends React.Component {
  constructor() {
    super();
    this.state = {
      list: [
        {
          id: 1,
          task: "Go home",
          completed: false
        },
        {
          id: 2,
          task:
            "Take test Note that the development build is not optimized. Note that the development build is not optimized.",
          completed: false
        },
        {
          id: 3,
          task: "Perform",
          completed: false
        },
        {
          id: 1,
          task: "Go home",
          completed: false
        },
        {
          id: 2,
          task: "Take test",
          completed: false
        },
        {
          id: 3,
          task: "Perform",
          completed: false
        }
      ],
      task: {
        id: 0,
        task: "",
        completed: false
      },
      popupTarget: null
    };
  }
  handleClick = e => {
    this.setState({
      popupTarget: e.currentTarget
    });
  };

  render() {
    const listItems = this.state.list.map(item => (
      <div>
        <Item item={item} />
      </div>
    ));
    return (
      <div className="listMainContainer">
        <div className="listContainer">{listItems}</div>
        <Grid container>
          <Grid item md={10} sm={6} xs={3}></Grid>
          <Grid item md={2} sm={6} xs={9}>
            {this.state.popupTarget != null && (
              <SimplePopover popupTarget={this.state.popupTarget} />
            )}
            <Fab color="primary" aria-label="add" className="AddIcon">
              <AddIcon onClick={this.handleClick} />
            </Fab>
          </Grid>
        </Grid>
      </div>
    );
  }
}
export default TodoList;
